def getFileContent(addr):
    with open(addr) as f:
        fcontent = []
        lines = f.readlines()
        for line in lines:
            fcontent.append(line.split('\n')[0].strip())
    return fcontent    

def writeFileWithSet(addr,listC):
    toSet = set(listC)
    result = list(toSet)
    with open(addr,'a') as f:
        for i in range(len(result)):
            f.write(result[i]+'\n')
    print("Done! Total count: %d" % len(result))

