def toString(listC,joint,start=None,end=None):
    if start != None:
        rstr = start + str(listC[0])
    else:
        rstr = str(listC[0])
        
    for i in range(1,len(listC)):
        rstr = rstr + joint + str(listC[i]) 
    
    if end != None:
        rstr = rstr + end
    
    return rstr


